import { Link } from 'react-router-dom'
import logo from './logo1.png'
import { useContext, useEffect, useState } from 'react'
import { SidebarContext } from '../../contexts/SidebarContext'
import {MDBIcon} from 'mdb-react-ui-kit';

import Styles from '../navbar/Navbar.module.css'
import { CartContext } from '../../contexts/CartContext';

export default function Navbar () {
    const [isActive, setIsActive] = useState(false)
    const {isOpen, setisOpen } = useContext(SidebarContext);
    const { itemAmount } = useContext(CartContext)

    //event listener
    useEffect(() => {
        window.addEventListener('scroll', () => {
            Window.scrolly > 60 ? setIsActive(true) : setIsActive(false)
        })
    })

    return (
        <header className={`${isActive ? Styles.header : Styles.header}`}>
            <div className={Styles.logo}>
            <Link className={Styles.menu} to={"/"}>
                <img src={logo} alt="logo1"/>
            </Link>
            </div>
            <div className={Styles.display_menu}>

                <Link className={Styles.menu} to={"/"}>Home</Link>
                <Link className={Styles.menu} to={"/estore"}>EStore</Link>
                <Link className={Styles.menu} to={"/cart"}>Carrinho</Link>
                <Link className={Styles.menu} to={"/pedidos"}>Pedidos</Link>
            </div>
            <div onClick={() => setisOpen(!isOpen)} style={{cursor:'pointer'}}>
                <MDBIcon fas icon="shopping-bag" />
                <div className={Styles.amount}>{itemAmount}</div>
            </div>
        </header>
    )
}
