import { useContext } from 'react';
import { useParams} from 'react-router-dom'
import {CartContext} from '../contexts/CartContext'
import {ProductContext} from '../contexts/ProductContext'

const ProductDetails = () => {
    const { id } = useParams()
    const { products } = useContext(ProductContext)
    const { addToCart } = useContext(CartContext)

    //get o produto by ID
    const product = products.find((item) => {
        return item.id === parseInt(id);
    })

    if (!product){
        return (
            <section>Carregando...</section>
        )
    }

    //destructure product
    const { title, price, description, image } = product

    return (
        <section style={{paddingTop:'32px', paddingBottom:'12px', height:'100vh', width:'80%', display:'flex', alignItems:'center'}}>
            <div>
                <div style={{display:'flex', columns:'flex', alignItems:'center', marginBottom:'8', justifyContent:'center'}}>
                <div><img style={{maxWidth:'200px'}} src={image} alt=''></img></div>
                <div style={{textAlign:'left'}}>
                    <h1 style={{fontSize:'medium', marginBottom:'2px', maxWidth:'450px'}}>{title}</h1>
                    <div style={{marginBottom:'10px'}}>R$ {price}</div>
                    <p>{description}</p>

                    <button onClick={()=> addToCart(product, product.id)}> Adicionar ao carrinho</button>
                </div>
                </div>
            </div>
        </section>
    )
}

export default ProductDetails
