import React from 'react';
import Styles from '../galery/Carousel.module.css'
import banner1 from '../../assets/banner1.png'
import banner2 from '../../assets/banner2.png'
import banner3 from '../../assets/banner3.png'

export default function Carousel() {
  let slideIndex = 1;
showSlides(slideIndex);

function plusSlides(n) {
  showSlides(slideIndex += n);
}

function currentSlide(n) {
  showSlides(slideIndex = n);
}

function showSlides(n) {
  let i;
  let slides = document.getElementsByClassName("mySlides");
  let dots = document.getElementsByClassName("dot");
  if (n > slides.length) {slideIndex = 1}    
  if (n < 1) {slideIndex = slides.length}
  for (i = 0; i < slides.length; i++) {
    slides[i].style.display = "none";  
  }
  for (i = 0; i < dots.length; i++) {
    dots[i].className = dots[i].className.replace(" active", "");
  }
  //slides[slideIndex-1].style={{display='block'}};  
  dots[slideIndex-1].className += " active";
}
  return (
    <div>
    <div className={Styles.slideshow_container}>

      <div className={Styles.mySlides}>
        <div className={Styles.numbertext}>1 / 3</div>
        <img src={banner1} alt="banner1" width={100}/>
      </div>

      <div className={Styles.mySlides}>
        <div className={Styles.numbertext}>2 / 3</div>
        <img src={banner2} alt="banner2" width={100}/>
      </div>

      <div className={Styles.mySlides}>
        <div className={Styles.numbertext}>3 / 3</div>
        <img src={banner3} alt="banner3" width={100}/>
      </div>

      <button className={Styles.prev} onclick={plusSlides(-1)}></button>
      <button className={Styles.next} onclick={plusSlides(1)}></button>

    </div>


      <div style={{textAlign:'center'}}>
        <span className={Styles.dot} onclick={currentSlide(1)}></span> 
        <span className={Styles.dot} onclick={currentSlide(2)}></span> 
        <span className={Styles.dot} onclick={currentSlide(3)}></span> 
      </div>
    </div>
  );
}
