import { useContext } from 'react';
import { useParams} from 'react-router-dom'
import {CartContext} from '../contexts/CartContext'
import {ProductContext} from '../contexts/ProductContext'

const ProductDetails = () => {
    const { id } = useParams()
    const { products } = useContext(ProductContext)
    const { addToCart } = useContext(CartContext)

    //get o produto by ID
    const product = products.find((item) => {
        return item.id === parseInt(id);
    })

    if (!product){
        return (
            <section>Carregando...</section>
        )
    }

    //destructure product
    const { title, price, description, image } = product

    return (
        <div>
            <section style={{paddingTop:'32px', paddingBottom:'12px', height:'100vh', width:'80%', display:'flex', alignItems:'center', justifyContent:'center'}}>
                <div style={{maxWidth:'auto'}}>
                    <div style={{display:'flex', columns:'flex', alignItems:'center'}}>
                    <div><img src={image} alt=''></img></div>
                    <h1>{title}</h1>
                    <div>R$ {price}</div>
                    <p>{description}</p>

                    <button onClick={()=> addToCart(product, product.id)}> Adicionar ao carrinho</button>
                    </div>
                </div>
            </section>

        </div>
    )
}

export default ProductDetails
