import { Link } from "react-router-dom";

const CartItem = ({ item }) => {
    //destructure Item
    const {id, title, image, price, amount } = item;
    return ( 
        <div>
            <div>
                <Link to={`/product/${id}`}>
                    <img src={image} alt=''/>
                </Link>
            </div>
        </div>
    )
}

export default CartItem;
