import { useState, useEffect } from 'react';

import axios from 'axios';
import { MDBCard,
    MDBCardBody,
    MDBBadge,
    MDBCol,
    MDBRow } from 'mdb-react-ui-kit';

export default function Pedidos() {

    const [scheduleList, setScheduleList] = useState([])


    useEffect(() => {
        axios.get('https://fakestoreapi.com/products')
            .then(res => setScheduleList(res.data.products))
            .catch(error => console.log(error))
    }, [])



    return (
        <div style={{marginBottom: '70px'}}>
            <div><h1 style={{fontWeight: 'bold', color: 'black', margin: '40px'}}>Lista de Agendamentos</h1></div>
            <section>
                {scheduleList.map((product) => {
                    return (
                        <div key={product.id}>
                            <MDBRow>
                                <MDBCol xl={6} className='mb-4'  style={{margin: 'auto'}}>
                                    <MDBCard>
                                        <MDBCardBody>
                                            <div className='d-flex align-items-center'>
                                                <div className='ms-3'>
                                                   <img src={product.image} alt="imagem de roupas"/>
                                                    <p className='text-muted mb-0'>{product.title}</p>
                                                    <p className='text-muted mb-0'>{product.price}</p>


                                                </div>

                                                <MDBBadge pill color='success' light 
                                                    style={{margin: 'auto', marginLeft:'80px', width:'100px', padding:'15px'}}>
                                                    EDITAR
                                                </MDBBadge>
                                               <MDBBadge pill color='danger' light 
                                                    style={{margin: 'auto', width:'100px', padding:'15px'}}
                                                    onClick={(e)=>{handleDelete(product.id, e)}}>
                                                    DELETAR
                                                </MDBBadge>
                                            </div>

                                        </MDBCardBody>
                                    </MDBCard>
                                </MDBCol>
                            </MDBRow>
                        </div>
                        ) //Final do Return

                    })}
            </section> 


        </div>
    )
}
