import React, { useContext } from 'react';

import { Link } from 'react-router-dom';
import CartItem from '../components/CartItem';
import { SidebarContext } from '../contexts/SidebarContext';
import { CartContext } from '../contexts/CartContext';
import Styles from '../components/Sidebar.module.css'
import { MDBIcon, MDBBtn } from 'mdb-react-ui-kit';

const Sidebar = () => {
  const { isOpen, handleClose } = useContext(SidebarContext)
  const { cart, clearCart, total, itemAmount } = useContext(CartContext);

  return (
    <div className={Styles.sidebar}>
      <div className={`${ isOpen ? Styles.open1 : Styles.open2 }`}>
        Carrinho ({itemAmount})
         {/* Bot√£o para voltar */}
        <div
          onClick={handleClose} style={{cursor:'pointer', textAlign:'right'}}>
          <MDBIcon fas icon="angle-double-right" />
        </div>
      
      <div className={Styles.cart}>
        {cart.map((item) => {
          return <CartItem item={item} key={item.id} />
        })}
      </div>

        <div>
          {/* Total da compra */}
          <div>
            <span>TOTAL:</span> R$ {parseFloat(total).toFixed(2)}
          </div>
           {/* Limpar carrinho inteiro */}
           <div onClick={clearCart} className={Styles.trash}>
            <MDBIcon fas icon="trash-alt" />
           </div>
        </div>
        <div>
              <Link to={'/'} className="d-grid gap-2 col-6 mx-auto">
                <MDBBtn className='text-dark' color='light'>
                  Ver carrinho
                </MDBBtn>
              </Link>
              <Link to={'/'} className="d-grid gap-2 col-6 mx-auto">
                <MDBBtn color='dark'>
                  Comprar 
                </MDBBtn>
              </Link>
        </div>
       </div>
    </div>
  );
};

export default Sidebar;
