import React from 'react';
import {
  MDBCard,
  MDBCardImage,
  MDBCardBody,
  MDBCardTitle,
  MDBCardText,
  MDBRow,
  MDBIcon,
  MDBCol
} from 'mdb-react-ui-kit';

import Styles from '../components/Box.module.css'

export default function Box({ products = [] }) {

  return (
    <div className={Styles.grid_container}>
      {
        products.map((product) => {
          console.log(product, 'product')
          const {id, title, price, image } = product;
          return (
            <MDBRow className='row-cols-1 row-cols-md-3 g-4'>
            <MDBCol>
              <MDBCard>
                <MDBCardImage
                  src={image}
                  alt='...'
                  position='top'
                />
                <MDBCardBody style={{textAlign:"left"}}>
                    <div>
                      <MDBIcon far icon="square" style={{margin:"4px"}}/>
                      <MDBIcon fas icon="square" style={{margin:"4px"}}/>
                      <MDBIcon fas icon="square" style={{margin:"4px"}}/>
                    </div><br></br>
                  <MDBCardText><h6>{title}</h6> </MDBCardText>
                  <MDBCardTitle><strong>R${price}</strong></MDBCardTitle>
                  <MDBCardText><small className='text-muted'>6x de R$120,00</small></MDBCardText>
                </MDBCardBody>
              </MDBCard>
            </MDBCol>

          </MDBRow>
          )
        })
      }

    </div>
  );
}
