import React, { useContext } from "react";
import { Link } from "react-router-dom";
import { MDBIcon } from 'mdb-react-ui-kit';
import Styles from '../components/CartItem.module.css'
import { CartContext } from "../contexts/CartContext";

const CartItem = ({ item }) => {
    const { removeFromCart, increaseAmount, decreaseAmount } = useContext(CartContext)

    //destructure Item
    const {id, title, image, price, amount } = item;
    return ( 
        <div className={Styles.contanier}>
            <div>
            {/* imagem produto */}
            <div>
                <Link to={`/product/${id}`}>
                    <img className={Styles.imagem} src={image} alt=''/>
                </Link>
            </div>

             {/* titulo produto*/}
            <div className={Styles.title}>
                <Link style={{color: 'black'}} to={`/product/${id}`}>
                    {title}
                </Link>
            </div>
            </div>
            <div className={Styles.contanier2}>
                {/* Quantidade */}
                <div className={Styles.qtd}>
                    <div onClick={() => decreaseAmount(id)}
                    style={{cursor:'pointer', padding:'10px', fontSize:'12px', border:'1px red solid'}}>
                        <MDBIcon fas icon="minus" />
                    </div>
                    <div style={{padding:'9px', fontSize:'15px'}}><strong>{amount}</strong></div>
                    <div onClick={() => increaseAmount(id)}
                    style={{cursor:'pointer', padding:'10px', fontSize:'12px'}}>
                        <MDBIcon fas icon="plus" />
                    </div>
                </div>
                {/* Preço Produto */}
                <div>R$ {price}</div>
                {/* Preço final do produto*/}
                <div>{`R$ ${parseFloat(price * amount).toFixed(2)}`}</div>
            </div>
            {/* botão para remover produto */}
            <div onClick={() => removeFromCart(id)}
            className={Styles.close}>
                <MDBIcon fas icon="times" />
            </div>
        </div>
    )
}

export default CartItem;
