import React from 'react';
import { useState } from "react";
import axios from 'axios';
import Styles from '../form/Form.module.css'


export default function Form() {

  // Modelo (JSON)
  const [data, setData] = useState({
    nome: '',
    email: '',  
    telefone: '',
    data: '',
    mensagem: '',
  })

  const handleInput = (event) => {
    setData({...data, [event.target.name]: event.target.event})
  }

  function handleSubmit(event) {
    event.preventDefault()
    axios.post('http://127.0.0.1:5000/mensagem', {data})
    .then(response => console.log(response))
    .catch(err => console.log(err))
  }

  return (
    <div className={Styles.form}>
      <section>
        <form onSubmit={handleSubmit}>
          <label>Nome Completo</label>
          <input type='text' name='nome' placeholder='Seu nome completo'
            value={data.nome}
            onChange={handleInput} 
            required
          />

          <label>E-mail</label>
          <input type='email' name='email' placeholder='exemplo@gmail.com' 
            value={data.email}
            onChange={handleInput}
            required
          />

          <label>Telefone</label>
          <input type='tel' name='telefone' placeholder='11956989969' 
            value={data.telefone}
            onChange={handleInput}
            required
          />

          <label>Data</label>
          <input type='date' name='data' 
            value={data.data}
            onChange={handleInput}
            required
          />

          <label>Mensagem</label>
          <textarea type='textarea' name='mensagem' placeholder='Mensagem' 
            value={data.msg}
            onChange={handleInput}
            required
          />

        <br></br><br></br>

          <button>ENVIAR</button>

        </form>
      </section>

    </div>
  );
}
